import React, { Component } from 'react';
import './App.css';
import "bootstrap/dist/css/bootstrap.css"



class App extends Component {
  state = { 
    num1: 0,
    num2: 0,
    total: 0
   }

  render() { 
    return ( 
      <div>
        <div>
          <h1>Total</h1><span>{this.state.total}</span>
        </div>
        <div>
          <h1>Player1</h1><span class="badge badge-secondary">{this.state.num1}</span>
          <button type="button" class="btn btn-secondary">Increment</button>
        </div>
        <div>
          <h1>Player2</h1><span class="badge badge-primary">{this.state.num2}</span>
          <button type="button" class="btn btn-primary">Increment</button>
        </div>
      </div>
    );
  }
}

export default App;
