import React, { Component } from 'react';
import './App.css';
import "bootstrap/dist/css/bootstrap.css"



class App extends Component {
  state = { 
    num1: 0,
    num2: 0,
    total: 0
   }

  randomNumber(min, max) { 
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

   handleFirst = () => {
     console.log("increment")
    const number = this.randomNumber(1, 10)
    this.setState({num1: number})
    const summ = this.state.total + number
    this.setState({total: summ})

   }

   handleSecond = () => {
    console.log("decrement")
    const number = this.randomNumber(1, 10)
    this.setState({num2: number})
    const summ = this.state.total - number
    this.setState({total: summ})

   }

  render() { 
    return ( 
      <div>
        <div id="first">
          <h1>Total<p class="badge m-4 badge-primary">{this.state.total}</p></h1>
        </div>
        <div id="second">
          <h1 id="forth">Player1</h1><span class="badge m-4 badge-primary">{this.state.num1}</span>
          <button type="button" onClick={this.handleFirst} class="btn btn-secondary"><span>Increment</span></button>
        </div>
        <div id="third">
          <h1>Player2</h1><span class="badge m-4 badge-primary">{this.state.num2}</span>
          <button type="button" onCLick={this.handleSecond} class="btn btn-secondary"><span>Decrement</span></button>
        </div>
      </div>
    );
  }
}

export default App;
