import React, { Component } from 'react';
import './App.css';
import "bootstrap/dist/css/bootstrap.css"



class App extends Component {
  state = { 
    num1: 0,
    num2: 0,
    total: 0
   }

  randomNumber(min, max) { 
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

   handleFirst = () => {
    const number = this.randomNumber(1, 10)
    this.setState({num1: number})
    const summ = this.state.total + number
    this.setState({total: summ})

   }

   handleSecond = () => {
    const number = this.randomNumber(1, 10)
    this.setState({num2: number, total: this.state.total - number})

   }

  render() { 
    return ( 
      <div>
        <div>
          <h1>Total  <p class="badge badge-primary">{this.state.total}</p></h1>
        </div>
        <div>
          <h1>Player1  </h1><span class="badge badge-primary">{this.state.num1}</span>
          <button type="button" onClick={this.handleFirst} class="btn btn-secondary">Increment</button>
        </div>
        <div>
          <h1>Player2  </h1><span class="badge badge-primary">{this.state.num2}</span>
          <button type="button" onCLick={this.handleSecond} class="btn btn-secondary">Increment</button>
        </div>
      </div>
    );
  }
}

export default App;
